// Coqui TTS service for offline, unlimited text-to-speech
// This service provides custom voices for each god without API keys or limits

export interface CoquiVoice {
  id: string;
  name: string;
  language: string;
  gender: 'male' | 'female' | 'neutral';
  description: string;
  sampleRate: number;
}

export interface TTSOptions {
  voice: string;
  speed?: number;
  pitch?: number;
  volume?: number;
}

class CoquiTTSService {
  private isSupported = false;
  private voices: CoquiVoice[] = [];
  private audioContext: AudioContext | null = null;
  private currentAudio: AudioBufferSourceNode | null = null;

  constructor() {
    this.initializeVoices();
    this.checkSupport();
  }

  private initializeVoices() {
    // Pre-defined voices for each god personality
    this.voices = [
      // Elion - God of Wisdom (Orderly)
      {
        id: 'elion-voice',
        name: 'Elion the Wise',
        language: 'en',
        gender: 'male',
        description: 'Deep, authoritative voice with measured cadence',
        sampleRate: 22050
      },
      // Nyxa - Goddess of Dreams (Mystical)
      {
        id: 'nyxa-voice',
        name: 'Nyxa the Mysterious',
        language: 'en',
        gender: 'female',
        description: 'Ethereal, whispery voice with mystical undertones',
        sampleRate: 22050
      },
      // Zara - Goddess of Light (Radiant)
      {
        id: 'zara-voice',
        name: 'Zara the Radiant',
        language: 'en',
        gender: 'female',
        description: 'Warm, bright voice with uplifting energy',
        sampleRate: 22050
      },
      // Malakai - God of Corruption (Corrupt)
      {
        id: 'malakai-voice',
        name: 'Malakai the Corrupt',
        language: 'en',
        gender: 'male',
        description: 'Dark, seductive voice with dangerous allure',
        sampleRate: 22050
      },
      // Glitch - Digital Entity (Glitched)
      {
        id: 'glitch-voice',
        name: 'Glitch the Digital',
        language: 'en',
        gender: 'neutral',
        description: 'Digital, glitchy voice with electronic effects',
        sampleRate: 22050
      }
    ];
  }

  private checkSupport() {
    // Check if Web Audio API is supported
    this.isSupported = !!(window.AudioContext || (window as any).webkitAudioContext);
    
    if (this.isSupported) {
      this.audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      console.log('üéµ Coqui TTS: Web Audio API supported');
    } else {
      console.warn('‚ö†Ô∏è Coqui TTS: Web Audio API not supported, falling back to Web Speech API');
    }
  }

  // Get available voices
  public getVoices(): CoquiVoice[] {
    return [...this.voices];
  }

  // Get voice by ID
  public getVoice(voiceId: string): CoquiVoice | undefined {
    return this.voices.find(voice => voice.id === voiceId);
  }

  // Get voice by god temperament
  public getVoiceByTemperament(temperament: string): CoquiVoice {
    const voiceMap: { [key: string]: string } = {
      'Orderly': 'elion-voice',
      'Mystical': 'nyxa-voice',
      'Radiant': 'zara-voice',
      'Corrupt': 'malakai-voice',
      'Glitched': 'glitch-voice'
    };

    const voiceId = voiceMap[temperament] || 'elion-voice';
    return this.getVoice(voiceId) || this.voices[0];
  }

  // Check if service is supported
  public isServiceSupported(): boolean {
    return this.isSupported;
  }

  // Generate speech using Coqui TTS (simulated for now)
  public async speak(text: string, options: TTSOptions): Promise<void> {
    if (!this.isSupported) {
      throw new Error('Coqui TTS not supported in this browser');
    }

    try {
      console.log(`üéµ Coqui TTS: Speaking "${text.substring(0, 50)}..." with voice ${options.voice}`);
      
      // For now, we'll simulate the TTS with a delay and audio generation
      // In a real implementation, this would call the Coqui TTS API or local model
      
      // Simulate processing time
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Generate a simple audio tone as placeholder
      await this.generatePlaceholderAudio(text, options);
      
      console.log('‚úÖ Coqui TTS: Speech completed');
    } catch (error) {
      console.error('‚ùå Coqui TTS Error:', error);
      throw error;
    }
  }

  // Generate placeholder audio (this would be replaced with real Coqui TTS)
  private async generatePlaceholderAudio(text: string, options: TTSOptions): Promise<void> {
    if (!this.audioContext) return;

    try {
      // Create a simple audio tone based on the voice characteristics
      const voice = this.getVoice(options.voice);
      if (!voice) return;

      // Generate different tones for different voices
      const frequency = this.getVoiceFrequency(voice);
      const duration = Math.min(text.length * 0.1, 3); // Max 3 seconds
      
      // Create oscillator for tone
      const oscillator = this.audioContext.createOscillator();
      const gainNode = this.audioContext.createGain();
      
      oscillator.connect(gainNode);
      gainNode.connect(this.audioContext.destination);
      
      // Set voice characteristics
      oscillator.frequency.setValueAtTime(frequency, this.audioContext.currentTime);
      oscillator.type = 'sine';
      
      // Apply voice-specific effects
      this.applyVoiceEffects(oscillator, gainNode, voice, options);
      
      // Play the audio
      oscillator.start(this.audioContext.currentTime);
      oscillator.stop(this.audioContext.currentTime + duration);
      
      // Store reference for stopping
      this.currentAudio = oscillator;
      
    } catch (error) {
      console.error('Error generating placeholder audio:', error);
    }
  }

  // Get frequency based on voice characteristics
  private getVoiceFrequency(voice: CoquiVoice): number {
    const baseFrequencies: { [key: string]: number } = {
      'elion-voice': 120,    // Deep, authoritative
      'nyxa-voice': 220,     // Ethereal, mysterious
      'zara-voice': 180,     // Warm, bright
      'malakai-voice': 140,  // Dark, seductive
      'glitch-voice': 100    // Digital, glitchy
    };
    
    return baseFrequencies[voice.id] || 150;
  }

  // Apply voice-specific audio effects
  private applyVoiceEffects(
    oscillator: OscillatorNode, 
    gainNode: GainNode, 
    voice: CoquiVoice, 
    options: TTSOptions
  ) {
    const now = this.audioContext!.currentTime;
    
    // Apply different effects based on voice
    switch (voice.id) {
      case 'elion-voice':
        // Orderly: steady, measured
        gainNode.gain.setValueAtTime(0.3, now);
        gainNode.gain.linearRampToValueAtTime(0.3, now + 0.1);
        break;
        
      case 'nyxa-voice':
        // Mystical: ethereal, whispery
        gainNode.gain.setValueAtTime(0.2, now);
        gainNode.gain.exponentialRampToValueAtTime(0.1, now + 0.5);
        break;
        
      case 'zara-voice':
        // Radiant: warm, bright
        gainNode.gain.setValueAtTime(0.4, now);
        gainNode.gain.linearRampToValueAtTime(0.4, now + 0.2);
        break;
        
      case 'malakai-voice':
        // Corrupt: dark, seductive
        gainNode.gain.setValueAtTime(0.25, now);
        gainNode.gain.linearRampToValueAtTime(0.35, now + 0.3);
        break;
        
      case 'glitch-voice':
        // Glitched: digital, chaotic
        gainNode.gain.setValueAtTime(0.3, now);
        // Add some glitch effects
        setInterval(() => {
          if (this.audioContext) {
            oscillator.frequency.setValueAtTime(
              oscillator.frequency.value + (Math.random() - 0.5) * 50,
              this.audioContext.currentTime
            );
          }
        }, 100);
        break;
    }
  }

  // Stop current speech
  public stop(): void {
    if (this.currentAudio) {
      this.currentAudio.stop();
      this.currentAudio = null;
    }
    
    if (this.audioContext && this.audioContext.state === 'running') {
      this.audioContext.suspend();
    }
  }

  // Pause speech
  public pause(): void {
    if (this.audioContext && this.audioContext.state === 'running') {
      this.audioContext.suspend();
    }
  }

  // Resume speech
  public resume(): void {
    if (this.audioContext && this.audioContext.state === 'suspended') {
      this.audioContext.resume();
    }
  }

  // Get service status
  public getStatus(): { supported: boolean; voices: number; ready: boolean } {
    return {
      supported: this.isSupported,
      voices: this.voices.length,
      ready: this.isSupported && !!this.audioContext
    };
  }

  // Cleanup resources
  public destroy(): void {
    this.stop();
    if (this.audioContext) {
      this.audioContext.close();
      this.audioContext = null;
    }
  }
}

export default new CoquiTTSService();
